mapper.insert:
insert into schedule(
s_no, s_title, s_contents, s_startday, s_finishday, s_date,situation,s_type
)values(s_no_seq.nextval,#{s_title},#{s_contents},#{s_startday},#{s_finishday},sysdate,#{situation},#{s_type})

mapper.update:
update schedule set
s_title=#{s_title}, s_contents= #{s_contents},
s_startday=#{s_startday},s_finishday=#{s_finishday},
situation=#{situation}, s_type=#{s_type}
where s_no = #{s_no}
